package ch09;
import java.io.File;
public class Ch_9_9_SeparatorTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("系统路径分隔符（String）是："+File.pathSeparator);
		System.out.println("系统默认路径分隔符（char）是："+File.pathSeparatorChar);
		System.out.println("系统名称分隔符（String）是："+File.separator);
		System.out.println("系统默认名称分隔符（char)是:"+File.separatorChar);
	}

}
